import React, { useState } from 'react';
import { Upload, FileImage, CheckCircle, XCircle, Clock } from 'lucide-react';

const Dashboard = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [processingResult, setProcessingResult] = useState(null);

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setProcessingResult(null);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    
    setIsProcessing(true);
    
    try {
      // Create FormData for file upload
      const formData = new FormData();
      formData.append('file', selectedFile);
      
      // Call OCR API
      const response = await fetch('http://localhost:5000/api/process-invoice', {
        method: 'POST',
        body: formData,
      });
      
      const result = await response.json();
      
      if (result.success) {
        const ocrData = result.data;
        
        // Transform OCR data to match our UI format
        setProcessingResult({
          status: 'accepted', // You can add validation logic here
          amount: `$${parseFloat(ocrData.amount).toFixed(2)}`,
          vendor: ocrData.vendor,
          date: ocrData.date,
          category: ocrData.short_description,
          confidence: '95%', // You can calculate this based on OCR confidence
          extractedData: {
            invoiceNumber: ocrData.invoice_number,
            item: ocrData.item,
            amount: ocrData.amount,
            date: ocrData.date,
            vendor: ocrData.vendor,
            shortDescription: ocrData.short_description
          },
          rawOcrOutput: result.raw_output // Store raw OCR output for debugging
        });
      } else {
        // Handle OCR processing error
        setProcessingResult({
          status: 'rejected',
          error: result.error,
          extractedData: null
        });
      }
    } catch (error) {
      console.error('Error processing invoice:', error);
      setProcessingResult({
        status: 'rejected',
        error: 'Failed to process invoice. Please check if the OCR API is running.',
        extractedData: null
      });
    } finally {
      setIsProcessing(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'accepted':
        return <CheckCircle className="w-5 h-5 text-dark-success" />;
      case 'rejected':
        return <XCircle className="w-5 h-5 text-dark-error" />;
      default:
        return <Clock className="w-5 h-5 text-dark-warning" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'accepted':
        return 'text-dark-success';
      case 'rejected':
        return 'text-dark-error';
      default:
        return 'text-dark-warning';
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-dark-text mb-2">Bill Processing Dashboard</h1>
        <p className="text-dark-muted">Upload your bill images for automated processing and validation</p>
      </div>

      {/* Upload Section */}
      <div className="card mb-8">
        <h2 className="text-xl font-semibold text-dark-text mb-4">Upload Bill Image</h2>
        
        <div className="space-y-4">
          <div className="border-2 border-dashed border-dark-border rounded-lg p-8 text-center hover:border-dark-accent transition-colors duration-200">
            <FileImage className="w-12 h-12 text-dark-muted mx-auto mb-4" />
            <p className="text-dark-text mb-2">Drop your bill image here or click to browse</p>
            <p className="text-sm text-dark-muted mb-4">Supports JPG, PNG, PDF formats up to 10MB</p>
            <input
              type="file"
              accept="image/*,.pdf"
              onChange={handleFileSelect}
              className="hidden"
              id="file-upload"
            />
            <label
              htmlFor="file-upload"
              className="btn-primary cursor-pointer inline-block"
            >
              Choose File
            </label>
          </div>

          {selectedFile && (
            <div className="bg-dark-surface border border-dark-border rounded-lg p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <FileImage className="w-8 h-8 text-dark-accent" />
                  <div>
                    <p className="text-dark-text font-medium">{selectedFile.name}</p>
                    <p className="text-sm text-dark-muted">
                      {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                    </p>
                  </div>
                </div>
                <button
                  onClick={handleUpload}
                  disabled={isProcessing}
                  className="btn-primary flex items-center space-x-2"
                >
                  <Upload className="w-4 h-4" />
                  <span>{isProcessing ? 'Processing...' : 'Process Bill'}</span>
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Processing Status */}
      {isProcessing && (
        <div className="card mb-8">
          <div className="flex items-center space-x-4">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-dark-accent"></div>
            <div>
              <h3 className="text-lg font-semibold text-dark-text">Processing Bill...</h3>
              <p className="text-dark-muted">Extracting data and validating information</p>
            </div>
          </div>
        </div>
      )}

      {/* Processing Results */}
      {processingResult && (
        <div className="card">
          <div className="flex items-center space-x-3 mb-6">
            {getStatusIcon(processingResult.status)}
            <h2 className="text-xl font-semibold text-dark-text">Processing Results</h2>
            <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(processingResult.status)} bg-opacity-20`}>
              {processingResult.status.toUpperCase()}
            </span>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Basic Information */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold text-dark-text">Bill Information</h3>
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-dark-muted">Amount:</span>
                  <span className="text-dark-text font-medium">{processingResult.amount}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Vendor:</span>
                  <span className="text-dark-text font-medium">{processingResult.vendor}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Date:</span>
                  <span className="text-dark-text font-medium">{processingResult.date}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Category:</span>
                  <span className="text-dark-text font-medium">{processingResult.category}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Confidence:</span>
                  <span className="text-dark-text font-medium">{processingResult.confidence}</span>
                </div>
              </div>
            </div>

            {/* Extracted Data */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold text-dark-text">OCR Extracted Data</h3>
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-dark-muted">Invoice #:</span>
                  <span className="text-dark-text font-medium">{processingResult.extractedData.invoiceNumber}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Item:</span>
                  <span className="text-dark-text font-medium">{processingResult.extractedData.item}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Amount:</span>
                  <span className="text-dark-text font-medium">${parseFloat(processingResult.extractedData.amount).toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Date:</span>
                  <span className="text-dark-text font-medium">{processingResult.extractedData.date}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-dark-muted">Category:</span>
                  <span className="text-dark-text font-medium">{processingResult.extractedData.shortDescription}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Actions */}
          <div className="mt-6 pt-6 border-t border-dark-border">
            <div className="flex space-x-4">
              <button className="btn-primary">
                Save Bill
              </button>
              <button className="btn-secondary">
                Download Report
              </button>
              <button 
                className="btn-secondary"
                onClick={() => {
                  setSelectedFile(null);
                  setProcessingResult(null);
                }}
              >
                Process Another
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
